package wolfdungeon3d;

import java.util.HashSet;

import com.jogamp.newt.opengl.GLWindow;

import processing.core.PApplet;
import processing.core.PGraphics;
import processing.core.PVector;

public class Runner extends PApplet {
	static private final PVector MAIN_CTX_PERCENT = new PVector(1f, 0.8f);
	static private final PVector MAIN_CTX_POS = new PVector(0, 0);

	// State Handling
	PGraphics mainGraphicsCtx;
	PGraphics hudCtx;
	Game game;
	RunnerState state = RunnerState.MENU;
	MainMenu mainMenu;

	// Input Handling
	HashSet<Character> heldKeys = new HashSet<>();
	PVector mouseMovement = new PVector();
	PVector lastMousePosition = new PVector();

	static enum RunnerState {
		MENU, GAME, HELP, GAME_PAUSE
	}

	// State Handling //
	public void setState(RunnerState state) {
		this.state = state;
	}

	public RunnerState getState() {
		return state;
	}

	// Main update loop
	public void update() {
		switch (state) {
		case GAME:
			if (game == null) {
				game = new Game(this, (GLWindow) surface.getNative());
			} else {
				for (Character k : heldKeys) {
					game.keyHeld(k);
				}
				game.update();
			}
			break;
		default:
			break;
		}
	}

	////////////////////
	// Input Handling //
	////////////////////

	public void keyPressed() {
		if (!heldKeys.contains(key)) {
			heldKeys.add(key);
		}
		if (game != null) {
			game.keyPressed(key);
		}
	}

	public void keyReleased() {
		if (heldKeys.contains(key)) {
			heldKeys.remove(key);
		}
		if (game != null) {
			game.keyReleased(key);
		}
	}

	public void mouseClicked() {
		switch (state) {
		case MENU:
			mainMenu.handleOnClick(new PVector(mouseX, mouseY), new PVector(width, height));
		default:
		}
	}

	///////////////////////
	// PApplet Functions //
	///////////////////////

	public void setup() {
		frameRate(60);
		mainGraphicsCtx = createGraphics(width, (int) ((float) height * MAIN_CTX_PERCENT.y), PApplet.P3D);
		hudCtx = createGraphics(width, (int) ((float) height * MAIN_CTX_PERCENT.y), PApplet.P2D);
		Assets.createInstance(this);
		mainMenu = new MainMenu(this);
	}

	public void settings() {
		size(1920, 1080, PApplet.P3D);
		fullScreen();
	}

	public void draw() {
		update();
		background(0);
		switch (state) {
		case GAME:
			if (game != null) {
				mainGraphicsCtx.colorMode(PGraphics.ARGB);
				mainGraphicsCtx.beginDraw();
				mainGraphicsCtx.blendMode(PGraphics.BLEND);
				game.draw(mainGraphicsCtx, this.getGraphics());
				mainGraphicsCtx.endDraw();
				image(mainGraphicsCtx, MAIN_CTX_POS.x, MAIN_CTX_POS.y);
			}
			break;
		case MENU:
			mainMenu.draw(this, new PVector(mouseX, mouseY));
			break;
		default:
			break;
		}
	}

	public static void main(String[] args) {
		PApplet.main(new String[] { "wolfdungeon3d.Runner" });
	}
}
